import { useState } from 'react';
import './App.css';

function App() {
  const [gridSize, setGridSize] = useState(16);

  const handleInput = (e) => {
    setGridSize(e.target.value)
  }

  return (
    <div className="App">
      <div className="body">
        <h1>Etch-a-Sketch</h1>
          <div className='grid-container'>

          </div>
        <input type="number" onChange={handleInput} />
      </div>
    </div>
  );
}

export default App;
