import { useState, } from "react"

const Square = (props) => {
    const [active, setActive] = useState('transparent')

    useEffect((props) => {
        if (props.clear === true) {
        setActive('transparent')
        }
    })

    const handleEnter = (e) => {
        setActive('black')
    }

    let squareStyle = {
        backgroundColor: active
    }

    return (
        <div className="square" onMouseEnter={handleEnter} style={squareStyle}>

        </div>
    )
}

export default Square
