import { useEffect, useState } from 'react';
import './App.css';
import Square from './square';

function App() {
  const [gridSize, setGridSize] = useState(25);
  const [color, setColor] = useState('black');
  const [clear, setClear] = useState(false);

  const handleInput = (e) => {
    setGridSize(e.target.value)
  }

  const createGridArray = (num) => {
    let arr = [];
    for (let i = 0; i < num*num; i++) {
      arr.push(i);
    }
    return arr
  }
  let gridArray = createGridArray(gridSize);

  const makeGridStyleArray = (num) => {
    let arr = [];
    for (let i=0; i < num; i++){
      arr.push('1fr')
    }
    return arr.join(' ')
  }

  let gridStyleArray = makeGridStyleArray(gridSize)

  const gridStyle = {
    gridTemplateColumns: gridStyleArray,
  }

  const handleClear = () => {
    setClear(true)
  }

  useEffect(() => {
    setClear(false)
  },[clear])

  const handleColor = () => {

  }

  return (
    <div className="App">
      <div className="body">
        <h1>Etch-a-Sketch</h1>
        <button className="black">Black</button>
        <button className="red">Red</button>
          <div className='grid-container' style={gridStyle}>
            {gridArray.map((id) => {
              return (
                <Square clear={clear} key={id} />
              )
            })}
          </div>
        <input type="number" value={gridSize} onChange={handleInput} />
        <button className="clear" onClick={handleClear}>Clear</button>
      </div>
    </div>
  );
}

export default App;
