import './App.css';
import Squares from './squares';

function App() {
  const [gridSize, setGridSize] = useState(16);

  const handleInput = (e) => {
    setGridSize(e.target.value)
  }

  const createGridArray = (num) => {
    let arr = [];
    for (let i = 0; i < num; i++) {
      arr.push(i);
    }
    return arr
  }
  let gridArray = createGridArray
  return (
    <div className="App">
      <div className="body">
        <h1>Etch-a-Sketch</h1>
          <div className='grid-container'>

          </div>
        <input type="number" value={gridSize} onChange={handleInput} />
      </div>
    </div>
  );
}

export default App;
