import { useState } from 'react';
import './App.css';


function App() {
  const [gridSize, setGridSize] = useState(5);

  const handleInput = (e) => {
    setGridSize(e.target.value)
  }

  const createGridArray = (num) => {
    let arr = [];
    for (let i = 0; i < num*num; i++) {
      arr.push(i);
    }
    return arr
  }
  let gridArray = createGridArray(gridSize);

  const makeGridStyleArray = (num) => {
    let arr = [];
    for (let i=0; i < num; i++){
      arr.push('1fr')
    }
    return arr.join(' ')
  }

  const gridStyle = {
    gridTemplateColumns: gridSize,

  }

  return (
    <div className="App">
      <div className="body">
        <h1>Etch-a-Sketch</h1>
          <div className='grid-container' style={gridStyle}>
            {gridArray.map((id) => {
              return (
                <li className="square" key={id}></li>
              )
            })}
          </div>
        <input type="number" value={gridSize} onChange={handleInput} />
      </div>
    </div>
  );
}

export default App;
