import { useEffect, useState } from "react"

const Square = () => {
    const [active, setActive] = useState(false)

    const handleEnter = (e) => {
        setActive(true)
    }

    let color;

    let squareStyle = {
        backgroundColor: color
    }

    useEffect(() => {
        color = 'transparent';
        if (active === true) {
            color = 'black'
        }
    })

    return (
        <div className="square" onMouseEnter={handleEnter} style={squareStyle}>

        </div>
    )
}

export default Square
